"use strict";var session=require("express-session");var connectRedis=require("connect-redis");var expressMysqlSession=require("express-mysql-session");function storeFactory(storeOptions){if(!storeOptions)return new session.MemoryStore;var storageType=storeOptions.storageType;if(typeof storageType!=="string"){throw new Error("must define storageType")}switch(storageType){case"redis":var RedisStore=connectRedis(session);return new RedisStore(storeOptions);case"mysql":var MySQLStore=expressMysqlSession(session);return new MySQLStore(storeOptions);default:throw new Error("invalid storageType ("+storageType+") must be redis or mysql")}}exports.storeFactory=storeFactory;